/* *  */package ar.com.santanderrio.obp.servicios.marcaviajero.entities;import java.util.List;/** * The Class TarjetaAsociada. */public class TarjetaAsociada {    /** The marca. */    private String marca;    /** The numero. */    private String numero;    /** The numero. */    private String ultimosCuatro;    /** The numero. */    private String nombre;    /** The alias. */    private String alias;        /** The is titular. */    private Boolean isTitular;    /** The fecha inhabilitada. */    private List<RangoInhabilitacion> fechaInhabilitada;        /**     * Instantiates a new tarjeta asociada.     */    public TarjetaAsociada() {        super();    }    /**	 * * * Instantiates a new tarjeta asociada. * * * * @param marca the marca *	 * * @param numero the numero * * @param nombre the nombre * * @param alias	 * the alias * * @param fechaInhabilitada the fecha inhabilitada * * @param	 * marca the marca * @param numero the numero * @param nombre the nombre	 * * @param alias the alias * @param fechaInhabilitada the fecha	 * inhabilitada	 *	 * @param marca	 *            the marca	 * @param numero	 *            the numero	 * @param nombre	 *            the nombre	 * @param alias	 *            the alias	 * @param fechaInhabilitada	 *            the fecha inhabilitada	 * @param isTitular	 *            the is titular	 */    public TarjetaAsociada(String marca, String numero, String nombre, String alias, List<RangoInhabilitacion> fechaInhabilitada, Boolean isTitular) {        super();        this.marca = marca;        this.numero = numero;        this.nombre = nombre;        this.alias = alias;        this.fechaInhabilitada = fechaInhabilitada;        this.isTitular = isTitular;    }    /**	 * * * @return the marca. * * @return the marca	 *	 * @return the marca	 */    public String getMarca() {        return marca;    }    /**	 * * * @param marca * * the marca to set. * * @param marca the new marca	 *	 * @param marca	 *            the new marca	 */    public void setMarca(String marca) {        this.marca = marca;    }    /**	 * * * @return the numero. * * @return the numero	 *	 * @return the numero	 */    public String getNumero() {        return numero;    }    /**	 * * * @param numero * * the numero to set. * * @param numero the new numero	 *	 * @param numero	 *            the new numero	 */    public void setNumero(String numero) {        this.numero = numero;    }    /**	 * * * @return the alias. * * @return the alias	 *	 * @return the alias	 */    public String getAlias() {        return alias;    }    /**	 * * * @param alias * * the alias to set. * * @param alias the new alias	 *	 * @param alias	 *            the new alias	 */    public void setAlias(String alias) {        this.alias = alias;    }    /**	 * * * @return the fechaInhabilitada. * * @return the fecha inhabilitada	 *	 * @return the fecha inhabilitada	 */    public List<RangoInhabilitacion> getFechaInhabilitada() {        return fechaInhabilitada;    }    /**	 * * * @param fechaInhabilitada * * the fechaInhabilitada to set. * * @param	 * fechaInhabilitada the new fecha inhabilitada	 *	 * @param fechaInhabilitada	 *            the new fecha inhabilitada	 */    public void setFechaInhabilitada(            List<RangoInhabilitacion> fechaInhabilitada) {        this.fechaInhabilitada = fechaInhabilitada;    }    	/**	 * * Gets the nombre. * * @return the nombre	 *	 * @return the nombre	 */	public String getNombre() {		return nombre;	}	/**	 * * Sets the nombre. * * @param nombre the new nombre	 *	 * @param nombre	 *            the new nombre	 */	public void setNombre(String nombre) {		this.nombre = nombre;	}	/**	 * Gets the ultimos cuatro.	 *	 * @return the ultimos cuatro	 */	public String getUltimosCuatro() {        return ultimosCuatro;    }    /**	 * Sets the ultimos cuatro.	 *	 * @param ultimosCuatro	 *            the new ultimos cuatro	 */    public void setUltimosCuatro(String ultimosCuatro) {        this.ultimosCuatro = ultimosCuatro;    }    /**	 * Gets the checks if is titular.	 *	 * @return the checks if is titular	 */    public Boolean getIsTitular() {        return isTitular;    }    /**	 * Sets the checks if is titular.	 *	 * @param isTitular	 *            the new checks if is titular	 */    public void setIsTitular(Boolean isTitular) {        this.isTitular = isTitular;    }    /**	 * Gets the producto prioridad.	 *	 * @return the producto prioridad	 */    public Integer getProductoPrioridad() {        if("VISA".equalsIgnoreCase(marca)){            return 0;        }        if("AMEX".equalsIgnoreCase(marca)){            return 1;        }        return 2;    }}