/* *  */package ar.com.santanderrio.obp.servicios.marcaviajero.web.view;import java.util.List;/** * The Class TarjetaAsociadaView. */public class TarjetaAsociadaView {      /** The marca. */     private String marca;      /** The numero. */     private String numero;      /** The alias. */     private String alias;          /** The is titular. */     private Boolean isTitular;      /** The fecha inhabilitada. */     private List<RangoInhabilitacionView> fechaInhabilitada;          /**		 * * @return the marca.		 *		 * @return the marca		 */     public String getMarca() {         return marca;     }      /**		 * * @param marca the marca to set.		 *		 * @param marca		 *            the new marca		 */     public void setMarca(String marca) {         this.marca = marca;     }      /**		 * * @return the numero.		 *		 * @return the numero		 */     public String getNumero() {         return numero;     }      /**		 * * @param numero the numero to set.		 *		 * @param numero		 *            the new numero		 */     public void setNumero(String numero) {         this.numero = numero;     }      /**		 * * @return the alias.		 *		 * @return the alias		 */     public String getAlias() {         return alias;     }      /**		 * * @param alias the alias to set.		 *		 * @param alias		 *            the new alias		 */     public void setAlias(String alias) {         this.alias = alias;     }    /**	 * * @return the fechaInhabilitada.	 *	 * @return the fecha inhabilitada	 */    public List<RangoInhabilitacionView> getFechaInhabilitada() {        return fechaInhabilitada;    }    /**	 * * @param fechaInhabilitada the fechaInhabilitada to set.	 *	 * @param fechaInhabilitada	 *            the new fecha inhabilitada	 */    public void setFechaInhabilitada(            List<RangoInhabilitacionView> fechaInhabilitada) {        this.fechaInhabilitada = fechaInhabilitada;    }        /**	 * Gets the marca prioridad.	 *	 * @return the marca prioridad	 */    public Integer getMarcaPrioridad() {        if("VISA".equalsIgnoreCase(marca)){            return 0;        }        if("AMEX".equalsIgnoreCase(marca)){            return 1;        }        return 2;    }    /* (non-Javadoc)     * @see java.lang.Object#toString()     */    @Override    public String toString() {        return "TarjetaAsociadaView [marca=" + marca + ", numero=" + numero + ", alias=" + alias                + ", fechaInhabilitada=" + fechaInhabilitada + "]";    }    /**	 * Gets the checks if is titular.	 *	 * @return the checks if is titular	 */    public Boolean getIsTitular() {        return isTitular;    }    /**	 * Sets the checks if is titular.	 *	 * @param isTitular	 *            the new checks if is titular	 */    public void setIsTitular(Boolean isTitular) {        this.isTitular = isTitular;    }    } 