/**
 * The Class TopbarManagerTest.
 */
@RunWith(MockitoJUnitRunner.class)
public class TopbarManagerTest {

    /** The topbar home manager. */
    @InjectMocks
    private TopbarHomeManager topbarHomeManager = new TopbarHomeManagerImpl();

    /** The sesion cliente. */
    @Mock
    private SesionCliente sesionCliente;

    /** The controller home manager. */
    @Mock
    private ControllerHomeManager controllerHomeManager;

    /** The alias cuenta BO. */
    @Mock
    private AliasCuentaBO aliasCuentaBO = new AliasCuentaBOImpl();

    /** The respuesta factory. */
    @Spy
    private RespuestaFactory respuestaFactory = new RespuestaFactory();

    /** The Property Map. */
    @Mock
    private PropertyMap propertyMap = new PropertyMap();

    /** The modulo permiso bo . */
    @Mock
    private ModuloPermisoBO moduloPermisoBO;

    /** The legal BO. */
    @Mock
    private LegalBO legalBO;

    /** The microfrontends BO. */
    @Mock
    private MicrofrontendsBO microfrontendsBO;

    /** The sesion parametros. */
    @Mock
    private SesionParametros sesionParametros;

    /** The cuenta BO. */
    @Mock
    private CuentaBO CuentaBO = new CuentaBOImpl();

    @Before
    public void init() {
        when(moduloPermisoBO.obtenerModuloPermisoPorAccion(AccionController.IR_INICIO_SELECT_ONLINE)).thenReturn(Mockito.mock(ModuloPermiso.class));
        when(moduloPermisoBO.obtenerModuloPermisoPorAccion(AccionController.IR_EJECUTIVO_ONLINE_MICROFRONT)).thenReturn(Mockito.mock(ModuloPermiso.class));
    }

    /**
     * Obtener topbar test.
     * @throws DAOException
     */
    @Test
    public void obtenerTopbarTest() throws DAOException {

        Cliente cliente = new Cliente();
        Segmento segmento = new Segmento();
        ControllerView controllerView = new ControllerView();

        segmento.setDuo(true);
        segmento.setPyme(true);
        segmento.setSelect(true);
        segmento.setUniversidad(true);
        segmento.setiU(true);
        segmento.setOperadorEjecutivo(getMockOperadorEjecutivo());
        cliente.setSegmento(segmento);

        when(sesionCliente.getCliente()).thenReturn(cliente);
        when(moduloPermisoBO.obtenerModuloPermisoPorAccion(AccionController.IR_INICIO_SELECT_ONLINE).tienePermisoDeVisibilidad()).thenReturn(true);
        when(moduloPermisoBO.obtenerModuloPermisoPorAccion(AccionController.IR_EJECUTIVO_ONLINE_MICROFRONT).tienePermisoDeVisibilidad()).thenReturn(true);
        when(legalBO.obtenerLegalesPorCodigo(CodigoMensajeConstantes.CODIGO_EJECUTIVO_DIPONIBLE)).thenReturn("");
        when(controllerHomeManager.obtenerController(Matchers.any(Cliente.class))).thenReturn(controllerView);
        DetalleDocumentoDTO detalleDocumentoDTO = new DetalleDocumentoDTO();
        detalleDocumentoDTO.setNroDocumento("32323232333");
        when(aliasCuentaBO.obtenerDocumentoValidoDTO(cliente)).thenReturn(detalleDocumentoDTO);
        when(aliasCuentaBO.obtenerCuitPorDetalle(detalleDocumentoDTO)).thenReturn("23-32323232-3");
        Map<String, Object> propMap = new HashMap<String, Object>();
        propMap.put("MICROAPP_ACCOUNTS.PATH_LIST", "/cuentas");
        propMap.put("MICROAPP_ACCOUNTS.ACTION_LIST", "irInicioCuentas");
        propertyMap.setProperties(propMap);
        when(propertyMap.getProperties()).thenReturn(propMap);
        when(propertyMap.get("MICROAPP_ACCOUNTS.PATH_LIST")).thenReturn("/cuentas");
        when(propertyMap.get("MICROAPP_ACCOUNTS.ACTION_LIST")).thenReturn("irInicioCuentas");
        when(propertyMap.get("MICROAPP_ACCOUNTS.PERMISO_PREFIX")).thenReturn("microapp-accounts");
        when(propertyMap.get("MICROAPP_ACCOUNTS.FF")).thenReturn("ACCOUNTS");
        when(propertyMap.get("MICROAPP_ACCOUNTS.VALIDAR_MENUITEM_LIST")).thenReturn("0");
        when(propertyMap.get("FF.ACCOUNTS.LISTADO_SUFFIX")).thenReturn("Accounts");
        when(moduloPermisoBO.tienePermisoMostrar(AccionController.MICROAPP_ACCOUNTS_FF)).thenReturn(true);
        when(moduloPermisoBO.tienePermisoMostrar(AccionController.MICROAPP_ACCOUNTS_ALL)).thenReturn(false);
        when(moduloPermisoBO.tienePermisoMostrar(AccionController.IR_EJECUTIVO_ONLINE_MICROFRONT)).thenReturn(true);
        when(microfrontendsBO.consultaVisibilidadPorNup(Matchers.anyString(), Matchers.anyString(), Matchers.anyBoolean())).thenReturn(true);
        when(sesionParametros.getHabilitadoFF()).thenReturn(new HashMap<String, Boolean>());

        Respuesta<TopbarHomeView> respuesta = topbarHomeManager.obtenerTopbar();

        assertNotNull(respuesta);
        assertEquals(respuesta.getEstadoRespuesta(), EstadoRespuesta.OK);
        assertEquals(respuesta.getRespuesta().getIsDuo(), Boolean.TRUE);
        assertEquals(respuesta.getRespuesta().getIsPyme(), Boolean.TRUE);
        assertEquals(respuesta.getRespuesta().getIsSelect(), Boolean.TRUE);
        assertEquals(respuesta.getRespuesta().getIsUniversidad(), Boolean.TRUE);
        assertEquals(respuesta.getRespuesta().getIsIU(), Boolean.TRUE);
        assertEquals(respuesta.getRespuesta().getUnicaCuentaMf(), Boolean.FALSE);
        MicroFEView mf = new MicroFEView("irInicioCuentas", "/cuentas");
        List<MicroFEView> mfs = respuesta.getRespuesta().getMfs();
        assertEquals(mf.getPath(), mfs.get(0).getPath());
        assertEquals(mf.getAction(), mfs.get(0).getAction());
        assertEquals(Boolean.TRUE, respuesta.getRespuesta().getOperadorEjecutivo().getExecutiveOnlineMicrofrontIsEnabled());

    }

    /**
     * genera el mock del ejecutivo.
     *
     * @return mock ejecutivo
     */
    private OperadorEjecutivo getMockOperadorEjecutivo() {
        OperadorEjecutivo operadorEjecutivo = new OperadorEjecutivo();
        operadorEjecutivo.setFotoEjecutivo("data:image/png;base64,");
        operadorEjecutivo.setNombreEjecutivo("¡Hola! Soy <span>Boado, Nahuel Facundo</span>");
        operadorEjecutivo.setHorarioAtencion("<p>Mi horario de atención es de: 9:00 hs. a 16:30 hs.<p>");
        operadorEjecutivo.setHorarioAtencionInicio("");
        operadorEjecutivo.setHorarioAtencionFin("");
        operadorEjecutivo.setMensajeEjecutivoTransitorio(null);
        operadorEjecutivo.setWhatsappEjecutivo("+ 54 9 11 41213841");
        operadorEjecutivo.setTelefonoMasivo("0-810-333-1287");
        operadorEjecutivo.setEmail("miejecutivoselect@santander.com.ar");
        operadorEjecutivo.setIdEjecutivo("A300581");
        operadorEjecutivo.setDiaLaborable(Boolean.TRUE);
        operadorEjecutivo.setErrorOperador(Boolean.FALSE);
        return operadorEjecutivo;
    }



}
